     getCategorias, getPostres, getDesayuno,
    // POST
    addReceta,
    // PUT
    updateReceta,
    // DELETE
    deleteReceta,
    // gestión usuarios
    getUser, ejemploUserPaginado, addUsuario, updateUsuario, deleteUsuario,
    
};

